import React, {useState} from 'react';
import { useNavigate } from "react-router-dom";
// import {signupVerify} from "../Data/repository.js";
import { Modal, Container } from 'react-bootstrap';


const SignUpModal = (props) => {
  // const navigate = useNavigate();
  const [emailInput, setEmailInput] = useState("");
  const [usernameInput, setUsernameInput] = useState("");
  const [addressInput, setAddressInput] = useState("");
  const [passwordInput, setPasswordInput] = useState("");
  const [errorDetected, setErrorDetected] = useState(null);
  const handleSubmit = (event) => {
    event.preventDefault();
    props.onHide();
    return;
  }

  return (
    <Modal show={props.show} onHide={props.onHide}>
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Welcome to Brokie Hub
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email" className="control-label">Email</label>
            <input name="email" id="email" className="form-control" placeholder='example@gmail.com' onChange={e => setEmailInput(e.target.value)} required />
          </div>
          <div className="form-group">
            <label htmlFor="email" className="control-label">Username</label>
            <input name="email" id="email" className="form-control" placeholder='FrugalShopper221' onChange={e => setUsernameInput(e.target.value)} required />
          </div>
          <div className="form-group">
            <label htmlFor="email" className="control-label">Address</label>
            <input name="email" id="email" className="form-control" onChange={e => setAddressInput(e.target.value)} required />
          </div>
          <div className="form-group">
            <label htmlFor="email" className="control-label">Password</label>
            <input name="email" id="email" className="form-control" onChange={e => setPasswordInput(e.target.value)} required />
          </div>
          <div className="form-group">
            <input type="submit" className="btn btn-primary" value="Sign Up" />
          </div>
        </form>
      </Modal.Body>
      <Modal.Footer>
        {errorDetected != null &&
          <div className="form-group">
            <span className="text-danger">{errorDetected}</span>
          </div>}
      </Modal.Footer>
    </Modal>
  )
}

export default SignUpModal;
