import './App.css';
import LandingPageContent from './Components/LandingPageContent.js';
import Navigatebar from './Components/Navigatebar.js';
import Footer from './Components/Footer.js';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import ProductPage from './Components/ProductPage.js';
import SearchResultsPage from './Components/SearchResultsPage.js';
import CheckoutPage from './Components/CheckoutPage.js';
import UserProfilePage from './Components/UserProfilePage.js';
import CartPage from './Components/CartPage.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import { createContext, useContext, useState } from 'react';

export const UserContext = createContext(null);

function App() {
  const [user, setUser] = useState({});
  const [cart, setCart] = useState([]);
  const userStateObject = { user, setUser };

  const addToCart = (product) => {
    setCart(prevCart => [...prevCart, product]);
  };

  return (
    <>
      <UserContext.Provider value={userStateObject}>
        <Router>
          <Navigatebar />
          <Routes>
            <Route path="/" element={<LandingPageContent />} />
            <Route path="/ProductPage.js" element={<ProductPage cart={cart} addToCart={addToCart} />} />
            <Route path="/SearchResultsPage.js" element={<SearchResultsPage />} />
            <Route path="/CheckoutPage.js" element={<CheckoutPage />} />
            <Route path="/UserProfilePage.js" element={<UserProfilePage />} />
            <Route path="/CartPage.js" element={<CartPage cart={cart} />} />
            <Route path="/product/:productId" element={<ProductPage cart={cart} addToCart={addToCart} />} />
          </Routes>
          <Footer/>
        </Router>
      </UserContext.Provider>
    </>
  );
}

export default App;
