import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App.js';
import './SearchResultsPage.css';


const totalResults = 100;
const displayedResults = 10;

const productPage = (productID) => {
    window.location.href = `/product/${productID}`;
};

function getLowestPricedItem(productArray) {
    return productArray.sort((a, b) => a.price - b.price)[0];
}

const SearchResultsPage = () => {
    // const [products, setProducts] = useState([]);
    // const [loading, setLoading] = useState(true);
    // const [cart, setCart] = useState([]);
    // const navigate = useNavigate();
    // const urlString = window.location.search;
    // const params = new URLSearchParams(urlString);

    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();
    const urlString = window.location.search;
    const params = new URLSearchParams(urlString);
    //const { user } = useContext(UserContext);
    const [cart, setCart] = useState([]);

    const addProductToCart = async (product) => {
        try {
            const response = await fetch('http://localhost:8080/v1/carts/add', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    cartId: cart.id,
                    productId: product.id,
                    quantity: 1
                })
            });

            if (response.ok) {
                navigate('/cart');
            } else {
                console.error("Error adding product to cart:", await response.text());
            }
        } catch (error) {
            console.error("Failed to add product to cart:", error);
        }
    };


    useEffect(() => {
        async function fetchData() {
            if (params.has('search')){
                const search = params.get('search')
                try {
                    console.log("searching for: " + search)
                    const response = await fetch("http://localhost:8080/v1/products/search?name=" + search + "&category=0&supermarket=&priceUpper=0.0&priceLower=0.0&sortBy=&sortOrder=");
                    if (!response.ok) {
                        throw new Error('Failed to fetch data');
                    }
                    const data = await response.json();
                    setProducts(data);
                    setLoading(false);
                } catch (error) {
                    console.error("Failed to fetch products:", error);
                    setLoading(false);
                }
            }
            else{
                try {
                    const response = await fetch("http://localhost:8080/v1/products");
                    if (!response.ok) {
                        throw new Error('Failed to fetch data');
                    }
                    const data = await response.json();
                    setProducts(data);
                    setLoading(false);
                } catch (error) {
                    console.error("Failed to fetch products:", error);
                    setLoading(false);
                }
            }
        }
        fetchData();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    const handleAddToCart = async (product) => {
        try {
            // Fetch all the prices for the given product ID
            const priceResponse = await fetch(`http://localhost:8080/v1/products/price/${product.productID}`);
            const priceDataArray = await priceResponse.json();

            const lowestPricedItem = getLowestPricedItem(priceDataArray);

            if (lowestPricedItem) {
                const productWithLowestPrice = {
                    ...product,
                    price: lowestPricedItem.price,
                    supermarket: lowestPricedItem.supermarket
                };
                addProductToCart(productWithLowestPrice);  // Here is where we changed the function name
                navigate('/CartPage.js'); // This should probably be just '/cart' depending on your routes
            } else {
                console.error(`Failed to fetch price for productID: ${product.productID}`);
            }
        } catch (error) {
            console.error(`Error fetching price for productID: ${product.productID}`, error);
        }
    };



    return (
    <div className="tableWrapper">
        <div className="tableContainer">
                <div className="tableHeaderInfo">
                    Showing results for:
                </div>
                <div className="tableHeaderInfo">
                    Total results: {totalResults}
                </div>
                <div className="tableHeaderInfo">
                    Displaying: {displayedResults}
                </div>
            <div className="buttonsContainer">
                <button>
                    <img src="icons/SortIcon.png" alt="Sort" width="20" />
                    Sort by
                </button>
                <button>
                    <img src="icons/FilterIcon.png" alt="Filter" width="20" />
                    Filters
                </button>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Product Name</th>
                        <th>Size</th>
                        <th>Unit</th>
                        <th>Category ID</th>
                        <th>Add To Cart</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                            <tr key={product.productID}>
                                <td>
                                    <button onClick={() => productPage(product.productID)} style={{border: 'none', background: 'transparent'}}>
                                        <img src={'/products/' + product.productID + '.webp'} alt={"Image of " + product.productName} width={70} style={{border: '2px solid #000', borderRadius: '5px'}}/>
                                    </button>
                                </td>
                                <td>{product.productName}</td>
                                <td>{product.size}</td>
                                <td>{product.unit}</td>
                                <td>{product.category_id}</td>
                                <td><button onClick={() => handleAddToCart(product)}>Add To Cart</button></td>
                            </tr>
                        ))}
                </tbody>
            </table>
        </div>
    </div>
    );
};

export default SearchResultsPage;
