import React, {useState, useEffect} from 'react';
import { useParams } from 'react-router-dom';
import './CartPage.css';

const CartPage = ({ cart }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const { productId } = useParams(); 

    const productPhoto = "/Computer.jpg";
    const productName = "MacBook Pro Laptop";
    const individualPrice = 3500;

    const productPhoto1 = "/Maple.png";
    const productName1 = "Maple Syrup";
    const individualPrice1 = 7;

    const [quantity, setQuantity] = useState(1);
    const [total, setTotal] = useState(individualPrice * quantity);
    const [total1, setTotal1] = useState(individualPrice1 * quantity);

    const handleQuantityChange = (direction) => {
        let newQuantity = quantity;
        if (direction === "increase") {
            newQuantity++;
        } else if (direction === "decrease" && quantity > 1) {
            newQuantity--;
        }
        setQuantity(newQuantity);
        setTotal(individualPrice * newQuantity);
    }

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await fetch(`http://localhost:8080/v1/products/price/${productId}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const data = await response.json();
                setProducts(data);
                setLoading(false);
            } catch (error) {
                console.error("Failed to fetch products:", error);
                setLoading(false);
            }
        }
        fetchData();
    }, [productId]);
    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="cartWrapper">
            <tbody>
                {cart.map((product) => (
                    // Display product details here
                <tr>
                    <td> 
                        {Array.from(new Set(products.map(product => product.productID))).map(uniqueProductID => (
                            <img src={'/products/' + uniqueProductID + '.webp'} alt={"Image of" + (products.find(product => product.productID === uniqueProductID)?.productName || "Product")} width={70} style={{ border: '2px solid #000', borderRadius: '5px' }}/>
                        ))}
                    </td>
                    <td>{product.productName}</td>
                    <td><button className="removeBtn">Remove</button></td>
                    <td>
                        <span>Qty:</span>
                        <input type="number" value={quantity} readOnly className="quantityInput" /> 
                    </td>
                    <td>Total: ${product.price}</td> 
                </tr>
                ))}
            </tbody>
            <h2>Items in cart</h2>
            <table>
                <thead>
                    <tr>
                        <th>Product Photo</th>
                        <th>Product Name</th>
                        <th></th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td><img src={productPhoto} alt="product" width="200" /></td>
                        <td>{productName}</td>
                        <td><button className="removeBtn">Remove</button></td>
                        <td>
                            <span>Qty:</span>
                            <input type="number" value={quantity} readOnly className="quantityInput" />
                        </td>
                        <td>Total: ${total}</td>
                    </tr>
                    {products.map((product) => (
                    <tr>
                        <td> 
                            {Array.from(new Set(products.map(product => product.productID))).map(uniqueProductID => (
                                <img src={'/products/' + uniqueProductID + '.webp'} alt={"Image of" + (products.find(product => product.productID === uniqueProductID)?.productName || "Product")} width={70} style={{ border: '2px solid #000', borderRadius: '5px' }}/>
                            ))}
                        </td>
                        <td>{product.productName}</td>
                        <td><button className="removeBtn">Remove</button></td>
                        <td>
                            <span>Qty:</span>
                            <input type="number" value={quantity} readOnly className="quantityInput" /> 
                        </td>
                        <td>Total: ${product.price}</td> 
                    </tr>
                    ))}
                </tbody>
            </table>
            <div className="clearCartGroup">
                <button className="clearCart">Clear Cart</button>
                <span>Grand Total: ${total + total1}</span> 
            </div>
            <div className="buttonsGroup">
                <button className="continueBtn">Continue Shopping</button>
                <button className="checkoutBtn">Proceed To Checkout</button>
            </div>
        </div>
    );
}

export default CartPage;
