//import React from 'react';
import React, { useState, useEffect } from 'react';
import './SearchResultsPage.css';

const totalResults = 100;
const displayedResults = 10;

const SearchResultsPage = () => {
    const hardcodedProduct = {
        id: "test-laptop-id",
        photo: "/Computer.jpg",
        name: "MacBook Pro Laptop",
        unit: "kg",
        price: 3500
    };

    const [products, setProducts] = useState([hardcodedProduct]);
    const [searchTerm, setSearchTerm] = useState("Milk");

    useEffect(() => {
        searchProducts(searchTerm);
    }, [searchTerm]);

    function searchProducts(name) {
        const apiUrl = `/api/products/search?name=${name}`;

        fetch(apiUrl)
        .then(response => response.json())
        .then(data => {
            setProducts([hardcodedProduct, ...data]);
        })
        .catch(error => {
            console.error("Error fetching search results:", error);
        });
    }

    return (
        <div className="tableWrapper">
            <div className="tableContainer">
                <div className="tableHeaderInfo">
                    Showing results for: <strong>{searchTerm}</strong>
                </div>
                <div className="tableHeaderInfo">
                    Total results: {totalResults}
                </div>
                <div className="tableHeaderInfo">
                    Displaying: {displayedResults}
                </div>
            </div>
            <div className="buttonsContainer">
                <button>
                    <img src="icons/SortIcon.png" alt="Sort" width="20" />
                    Sort by
                </button>
                <button>
                    <img src="icons/FilterIcon.png" alt="Filter" width="20" />
                    Filters
                </button>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>Product Photo</th>
                        <th>Product Name</th>
                        <th>Unit</th>
                        <th>Price ($)</th>
                        <th>Add To Cart</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map(product => (
                        <tr key={product.id}>
                            <td><img src={product.photo} alt="product" width="200" /></td>
                            <td>{product.name}</td>
                            <td>{product.unit}</td>
                            <td>{product.price}</td>
                            <td><button>Add To Cart</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default SearchResultsPage;
