const SearchResultsPage = () => {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("Laptop");

    useEffect(() => {
        searchProducts(searchTerm);
    }, [searchTerm]);

    function searchProducts(name, category, supermarket, priceUpper, priceLower, sortBy = "DEFAULT", sortOrder = "ASC") {
        const apiUrl = "/api/products/search";
        let url = `${apiUrl}?name=${name}&category=${category}&supermarket=${supermarket}&priceUpper=${priceUpper}&priceLower=${priceLower}&sortBy=${sortBy}&sortOrder=${sortOrder}`;

        fetch(url)
        .then(response => response.json())
        .then(data => {
            setProducts(data);
        })
        .catch(error => {
            console.error("Error fetching search results:", error);
        });
    }

    return (
        <div className="tableWrapper">
            <div className="tableContainer">
                {}
            </div>
            <div className="buttonsContainer">
                {}
            </div>
            <table>
                <thead>
                    {}
                </thead>
                <tbody>
                    {products.map(product => (
                        <tr key={product.id}>
                            <td><img src={product.photo} alt="product" width="200" /></td>
                            <td>{product.name}</td>
                            <td>{product.storeCount}</td>
                            <td>{product.price}</td>
                            <td><button>Add To Cart</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}



export default SearchResultsPage;
