//import React from 'react';
import React, {useState, useEffect} from 'react';
import './ProductPage.css';
import { useParams, useNavigate } from 'react-router-dom';
import Table from 'react-bootstrap/Table';

function getLowestPricedItem(productArray) {
  return productArray.sort((a, b) => a.price - b.price)[0];
}

const ProductPage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const { productId } = useParams(); 
  const navigate = useNavigate();

  // const handleAddToCart = () => {
  //   if (products && products.length > 0) {
  //       addToCart(products[0]); // assuming the first product in the array is the one you want to add
  //       navigate('/CartPage.js');
  //   }
  // }

  const handleAddToCart = (productArray) => {
    const lowestPricedItem = getLowestPricedItem(productArray);
    addToCart(lowestPricedItem);
    navigate('/CartPage.js');
  }

  useEffect(() => {
    async function fetchData() {
        try {
            // Fetch product price details
            const responsePrice = await fetch(`http://localhost:8080/v1/products/price/${productId}`);
            if (!responsePrice.ok) {
                throw new Error('Failed to fetch price data');
            }
            const priceData = await responsePrice.json();

            // Fetch product name details (assuming there's an endpoint for this)
            const responseProduct = await fetch(`http://localhost:8080/v1/products/${productId}`);
            if (!responseProduct.ok) {
                throw new Error('Failed to fetch product data');
            }
            const productData = await responseProduct.json();

            // Combine the data
            const combinedData = {
                ...priceData,
                ...productData
            }

            setProducts([combinedData]); // Combining as an array, adjust as needed
            setLoading(false);
        } catch (error) {
            console.error("Failed to fetch product details:", error);
            setLoading(false);
        }
    }
    fetchData();
  }, [productId]);
  if (loading) {
      return <div>Loading...</div>;
  }

  return (
    <>
      <div className="product-display">
        <div className="imageBorder">
          <img src="/goats-weed.png" alt="Goat's Weed" />
          {Array.from(new Set(products.map(product => product.productID))).map(uniqueProductID => (
            <img src={'/products/' + uniqueProductID + '.webp'} alt={"Image of" + (products.find(product => product.productID === uniqueProductID)?.productName || "Product")} width={70} style={{ border: '2px solid #000', borderRadius: '5px' }}/>
            ))}
          <h2>Horny Goat Weed</h2>
          <i>Naturopathica</i><br></br>

          <button onClick={handleAddToCart}>Add to Cart</button>
        </div>
        <div className="block-display">
      <table>
                <thead>
                    <tr>
                        <th>Location</th>
                        <th>Today's Price ($)</th>
                    </tr>
                </thead>
                <tbody>
                  {products.map((product) => (
                    <tr key={product.productID}>
                        <td>{product.supermarket}</td>
                        <td>{product.price}</td>
                    </tr>
                  ))}
                </tbody>
            </table>
        </div>
      </div>
    </>
  )
}

export default ProductPage;
