import React, {useState, useEffect} from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './SearchResultsPage.css';

const totalResults = 100;
const displayedResults = 10;



const productPage = (productID) => {
    window.location.href = `/product/${productID}`;
};

const addToCart = () => {
    window.location.href = '/CartPage.js'; 
};

const SearchResultsPage = ({ addToCart }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true); 
    const navigate = useNavigate();

    const handleAddToCart = (product) => {
        addToCart(product);
        navigate('/CartPage.js');
    }

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await fetch("http://localhost:8080/v1/products");
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const data = await response.json();
                setProducts(data);
                setLoading(false);
            } catch (error) {
                console.error("Failed to fetch products:", error);
                setLoading(false);
            }
        }
        fetchData();
    }, []);
    if (loading) {
        return <div>Loading...</div>;
    }
    return (
    <div className="tableWrapper">
        <div className="tableContainer">
                <div className="tableHeaderInfo">
                    Showing results for:
                </div>
                <div className="tableHeaderInfo">
                    Total results: {totalResults}
                </div>
                <div className="tableHeaderInfo">
                    Displaying: {displayedResults}
                </div>
            <div className="buttonsContainer">
                <button>
                    <img src="icons/SortIcon.png" alt="Sort" width="20" />
                    Sort by
                </button>
                <button>
                    <img src="icons/FilterIcon.png" alt="Filter" width="20" />
                    Filters
                </button>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Product Name</th>
                        <th>Size</th>
                        <th>Unit</th>
                        <th>Category ID</th>
                        <th>Add To Cart</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                            <tr key={product.productID}>
                                <td>
                                    <button onClick={() => productPage(product.productID)} style={{border: 'none', background: 'transparent'}}>
                                        <img src={'/products/' + product.productID + '.webp'} alt={"Image of " + product.productName} width={70} style={{border: '2px solid #000', borderRadius: '5px'}}/>
                                    </button>
                                </td>
                                <td>{product.productName}</td>
                                <td>{product.size}</td>
                                <td>{product.unit}</td>
                                <td>{product.category_id}</td>
                                <button onClick={handleAddToCart}>Add to Cart</button>
                            </tr>
                        ))}
                </tbody>
            </table>
        </div>
    </div>
    );
};

export default SearchResultsPage;
