import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../App.js';
import './CartPage.css';

const CartPage = () => {
    const { user } = useContext(UserContext);
    const [cart, setCart] = useState([]);


    const navigate = useNavigate();
    const productPage = (productId) => {
        navigate(`/product/${productId}`);
    };

    const roundedQuantity = (num) => Math.round(num * 100) / 100;

    useEffect(() => {
        const fetchCartForUser = async () => {
            const response = await fetch(`http://localhost:8080/v1/carts/customer/${user.id}`);
            const data = await response.json();
            setCart(data);
        };
        fetchCartForUser();
    }, [user]);

    const addToCart = async (product) => {
        const response = await fetch('http://localhost:8080/v1/carts/add', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                cartId: cart.id,
                productId: product.id,
                quantity: 1
            })
        });

        if (response.ok) {
            const updatedCart = await response.json();
            setCart(updatedCart);
        }
    };

    const updateProductQuantity = async (productId, newQuantity) => {
        await fetch('http://localhost:8080/v1/carts/set', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                cartId: cart.id,
                productId,
                quantity: newQuantity
            })
        });

        const updatedProduct = cart.find(product => product.productId === productId);
        updatedProduct.quantity = newQuantity;
        setCart([...cart]);
    };

    const removeFromCart = async (productId) => {
        await fetch(`http://localhost:8080/v1/carts/delete`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                cartId: cart.id,
                productId
            })
        });

        const updatedCart = cart.filter(product => product.productId !== productId);
        setCart(updatedCart);
    };

    const clearCart = async () => {
        await fetch(`http://localhost:8080/v1/carts/clear/${user.id}`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
        });
        setCart([]);
    };

    //const grandTotal = cart.reduce((acc, product) => acc + product.price * product.quantity, 0);

    const updateQuantity = async (productID, newQuantity) => {
        if (newQuantity >= 1) {
          await updateProductQuantity(productID, newQuantity);
        }
    };

    return (
        <div className="cartWrapper">
            <tbody>
            </tbody>
            <h2>Items in cart</h2>
            <table>
                <thead>
                    <tr>
                        <th>Product Photo</th>
                        <th>Product Name</th>
                        <th></th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    {cart.map((product) => (
                        <tr key={product.productID}>
                            <td>
                                <button onClick={() => productPage(product.productID)} style={{border: 'none', background: 'transparent'}}>
                                    <img src={'/products/' + product.productID + '.webp'} 
                                        alt={"Image of " + product.productName} 
                                        width={70} 
                                        style={{border: '2px solid #000', borderRadius: '5px'}} />
                                </button>
                            </td>
                            <td>{product.productName}</td>
                            <td><button onClick={() => removeFromCart(product.productID)} className="removeBtn">Remove</button></td>
                            <td>
                                <span>Qty:</span>
                                <input type="number" value={product.quantity || 1} onChange={(e) => updateQuantity(product.productID, parseInt(e.target.value))} className="quantityInput" />
                            </td>
                            <td>Total: ${roundedQuantity(product.price * (product.quantity || 1))}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="clearCartGroup">
                <button onClick={clearCart} className="clearCart">Clear Cart</button>
                <span>Grand Total: ${roundedQuantity(grandTotal)}</span>
            </div>
            <div className="buttonsGroup">
                <button className="continueBtn">Continue Shopping</button>
                <button className="checkoutBtn">Proceed To Checkout</button>
            </div>
        </div>
    );
}

export default CartPage;
