    // const params = new URLSearchParams(urlString);

    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();
    const urlString = window.location.search;
    const params = new URLSearchParams(urlString);
    //const { user } = useContext(UserContext);
    const [cart, setCart] = useState([]);
    const { user } = useContext(UserContext);

    const navigateToProduct = (productID) => {
        navigate(`/product/${productID}`, { state: { user: user } });
    };


    useEffect(() => {
        console.log(user)
        console.log("NotWan");
        const fetchCartForUser = async () => {
            const response = await fetch(`http://localhost:8080/v1/carts/customer/${user.UserID}`);
            const data = await response.json();
            if ((data)) {
                console.log(data);

                setCart(data);
            } else {
                console.error("Expected cart data to be an array but received:", data);
                setCart([]); 
            }
        };
        fetchCartForUser();
    }, [user]);


    // const addProductToCart = async (product) => {
    //     console.log(cart)
    //     console.log("cunt")
    //     try {
    //         const response = await fetch('http://localhost:8080/v1/carts/add', {
    //             method: 'POST',
