    //         if (lowestPricedItem) {
    //             const productWithLowestPrice = {
    //                 ...product,
    //                 price: lowestPricedItem.price,
    //                 supermarket: lowestPricedItem.supermarket
    //             };
    //             addProductToCart(productWithLowestPrice);  // Here is where we changed the function name
