import React, {useState,useContext} from 'react';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import logo from './online-shopping.png';
import {UserContext} from '../App.js';
import './Navigatebar.css';
import { Link } from 'react-router-dom';

const Navigatebar = () => {
  const [searchValue, setSearchValue] = useState('');
  const handleChange = (e) => {
    setSearchValue(e.target.value);
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
        // Change this to search results page using searchValue
        window.location.href = `/SearchResultsPage.js?search=${searchValue}`;
    }
  }
  return (
    //<Container>
      <Navbar expand="lg" className="navbarStyle">
        <Navbar.Brand href="#">
          <img alt="" src={logo} width="40" height="40" className="d-inline-block align-top" />{' '}
        </Navbar.Brand>
        <Nav className="me-auto">
          <Link to="/" component={Nav.Link}>Home</Link>
          <Link to="/ProductPage.js" component={Nav.Link}>Products</Link>
          <Link to="/SearchResultsPage.js" component={Nav.Link}>Search Results</Link>
          <Link to="/CartPage.js" component={Nav.Link}>Cart</Link>
          <Link to="#" component={Nav.Link}>Login</Link>
          <Link to="/CheckoutPage.js" component={Nav.Link}>Checkout</Link>
          <Link to="/UserProfilePage.js" component={Nav.Link}>User profile</Link>
        </Nav>
        <div className="searchBarContainer">
          <input
          className="searchBar"
          type="search"
          value={searchValue}
          onChange={handleChange}
          onKeyPress={handleKeyDown}
          placeholder="Find your latest deal today. &#x1F50E; "
        />
        </div>
      </Navbar>
    //</Container>
  )
}

export default Navigatebar;
