//import React from 'react';
import React, {useState, useEffect} from 'react';
import './ProductPage.css';
import { useParams, useNavigate } from 'react-router-dom';
import Table from 'react-bootstrap/Table';

function getLowestPricedItem(productArray) {
  return productArray.sort((a, b) => a.price - b.price)[0];
}

const ProductPage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const { productId } = useParams(); 
  const navigate = useNavigate();
  const [error, setError] = useState(null);

  const handleAddToCart = (productArray) => {
    const lowestPricedItem = getLowestPricedItem(productArray);
    addToCart(lowestPricedItem);
    navigate('/CartPage.js');
  }

  useEffect(() => {
  let isMounted = true;

  async function fetchData() {
    try {
      const responsePrice = await fetch(`http://localhost:8080/v1/products/price/${productId}`);
      const responseProduct = await fetch(`http://localhost:8080/v1/products/${productId}`);

      if (!responsePrice.ok || !responseProduct.ok) {
        throw new Error('Failed to fetch data');
      }

      const priceData = await responsePrice.json();
      const productData = await responseProduct.json();

      if (isMounted) {
        setProducts(priceData);  // 3. Proper checks on fetch response
        setLoading(false);
      }
    } catch (error) {
      if (isMounted) {
        setError(error.toString()); // 1. Set the error state
        setLoading(false);
      }
    }
  }

  fetchData();

  // Cleanup
  return () => {
    isMounted = false;
  };
  }, [productId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {  // 1. Display the error
    return <div>Error: {error}</div>;
  }


  return (
    <>
      <div className="product-display">
        <div className="imageBorder">
          <img src="/goats-weed.png" alt="Goat's Weed" />
          {Array.from(new Set(products.map(product => product.productID))).map((uniqueProductID, index) => (
            <img key={index} src={'/products/' + uniqueProductID + '.webp'} alt={"Image of" + (products.find(product => product.productID === uniqueProductID)?.productName || "Product")} width={70} style={{ border: '2px solid #000', borderRadius: '5px' }} />
          ))}

          <h2>{products[0]?.productName || "Product Name"}</h2>
          <i>Naturopathica</i><br></br>

          <button onClick={() => handleAddToCart(products)}>Add to Cart</button>
        </div>
        <div className="block-display">
      <table>
                <thead>
                    <tr>
                        <th>Location</th>
                        <th>Today's Price ($)</th>
                    </tr>
                </thead>
                <tbody>
                  {products.map((product) => (
                    <tr key={product.productID}>
                        <td>{product.supermarket}</td>
                        <td>{product.price}</td>
                    </tr>
                  ))}
                </tbody>
            </table>
        </div>
      </div>
    </>
  )
}

export default ProductPage;
