import React, {useState,useContext} from 'react';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import logo from './online-shopping.png';
import {UserContext} from '../App.js';
import './Navigatebar.css';

const Navigatebar = () => {
  const [searchValue, setSearchValue] = useState('');
  const handleChange = (e) => {
    setSearchValue(e.target.value);
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
        // Change this to search results page using searchValue
        window.location.href = `/SearchResultsPage.js?search=${searchValue}`;
    }
  }
  return (
    <Container>
      <Navbar expand="lg" className="navbarStyle">
        <Navbar.Brand href="#">
          <img
            alt=""
            src={logo}
            width="40"
            height="40"
            className="d-inline-block align-top" />{' '}
        </Navbar.Brand>
        <Nav className="me-auto">
          <Nav.Link href="/">Home</Nav.Link>
          <Nav.Link href="/ProductPage.js">Products</Nav.Link>
          <Nav.Link href="/SearchResultsPage.js">Search Results</Nav.Link>
          <Nav.Link href="/CartPage.js">Cart</Nav.Link>
          <Nav.Link href="#">Login</Nav.Link>
          <Nav.Link href="/CheckoutPage.js">Checkout</Nav.Link>
          <Nav.Link href="/UserProfilePAge.js">User profile</Nav.Link>
        </Nav>
        <div className="searchBarContainer">
          <input
          className="searchBar"
          type="search"
          value={searchValue}
          onChange={handleChange}
          onKeyPress={handleKeyDown}
          placeholder="Find your latest deal today. &#x1F50E; "
        />
        </div>
      </Navbar>
    </Container>
  )
}

export default Navigatebar;
