import './App.css';
import LandingPageContent from './Components/LandingPageContent.js';
import Navigatebar from './Components/Navigatebar.js';
import Footer from './Components/Footer.js';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import ProductPage from './Components/ProductPage.js';
import SearchResultsPage from './Components/SearchResultsPage.js';
import CheckoutPage from './Components/CheckoutPage.js';
import UserProfilePage from './Components/UserProfilePage.js';
import CartPage from './Components/CartPage.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import { createContext, useEffect, useState, useContext } from 'react';

export const UserContext = createContext({
  user: null,
  setUser: () => {}
});

export const CartContext = createContext({
  cart: [],
  addToCart: () => {},
  removeFromCart: () => {},
  updateProductQuantity: () => {},
  clearCart: () => {}
});


const updateLocalStorage = (key, value) => {
  localStorage.setItem(key, JSON.stringify(value));
}

function App() {
  const [user, setUser] = useState({id: null});
  const userStateObject = { user, setUser };

  const cartKey = user && user.id ? `cart_${user.id}` : 'cart';

  const [cart, setCart] = useState(() => {
    return JSON.parse(localStorage.getItem(cartKey)) || [];
  });



  useEffect(() => {
    if (user && user.id) {
      updateLocalStorage(cartKey, cart);
    }
  }, [cart, user]);



  const addToCart = async (product) => {
    //if (!user || !user.id) return;
    setCart(prevCart => {
        const existingProduct = prevCart.find(p => p.productID === product.productID);
        if (existingProduct) {
            // Increase the quantity for existing products
            return prevCart.map(p => 
                p.productID === product.productID 
                ? { ...p, quantity: p.quantity + 1 }
                : p
            );
        } else {
            // Add new product with a quantity of 1
            return [...prevCart, { ...product, quantity: 1 }];
        }
    });

    // const response = await fetch('/api/cart/add', {
    //   method: 'POST',
    //   headers: { 'Content-Type': 'application/json' },
    //   body: JSON.stringify({ userId: user.id, productID: product.productID })
    // });

    //setCart((prevState) => [...prevState, product]);

    const response = await fetch("http://localhost:8080/v1/carts", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({cartID: user.id, product}),
    });

    // Assume the server returns the updated cart
    const updatedCart = await response.json();
    setCart(updatedCart);
  };

  const updateProductQuantity = async (productID, newQuantity) => {
    //if (!user || !user.id) return;
    setCart(prevCart => prevCart.map(product => 
        product.productID === productID 
        ? { ...product, quantity: newQuantity }
        : product
    ));

    await fetch(`/api/cart/update/${productID}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId: user.id, newQuantity })
    });

  };

  const removeFromCart = async (productIDToRemove) => {
    //if (!user || !user.id) return;
    const updatedCart = cart.filter(product => product.productID !== productIDToRemove);
    setCart(updatedCart);

    await fetch(`/api/cart/remove/${productIDToRemove}`, {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId: user.id })
    });
  }

  const clearCart = async () => {
    //if (!user || !user.id) return;
    setCart([]);

    await fetch('/api/cart/clear', {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId: user.id })
    });
  }

  const cartContextValue = {
    cart,
    addToCart,
    removeFromCart,
    updateProductQuantity,
    clearCart
  };

  return (
    <>
      <UserContext.Provider value={userStateObject}>
      <CartContext.Provider value={cartContextValue}>
        <Router>
          <Navigatebar/>
          <Routes>
            <Route path="/" element={<LandingPageContent />} />
            <Route path="/ProductPage.js" element={<ProductPage cart={cart} addToCart={addToCart} />} />
            <Route path="/SearchResultsPage.js" element={<SearchResultsPage cart={cart} addToCart={addToCart}/>} />
            <Route path="/CheckoutPage.js" element={<CheckoutPage />} />
            <Route path="/UserProfilePage.js" element={<UserProfilePage />} />
            <Route path="/CartPage.js" element={<CartPage cart={cart} updateProductQuantity={updateProductQuantity} removeFromCart={removeFromCart} clearCart={clearCart} />} />
            <Route path="/product/:productId" element={<ProductPage cart={cart} addToCart={addToCart} />} />
            <Route path="/search/:productId" element={<SearchResultsPage cart={cart} addToCart={addToCart}/>} />
          </Routes>
          <Footer/>
        </Router>
        </CartContext.Provider>
      </UserContext.Provider>
    </>
  );
}

export default App;
