import React, {useState, useEffect} from 'react';
import { useParams } from 'react-router-dom';
import './CartPage.css';


const addToCart = (product) => {
    setCart(prevCart => {
        const existingProduct = prevCart.find(p => p.productID === product.productID);
        if (existingProduct) {
            // Increase the quantity for existing products
            return prevCart.map(p => 
                p.productID === product.productID 
                ? { ...p, quantity: p.quantity + 1 }
                : p
            );
        } else {
            // Add new product with a quantity of 1
            return [...prevCart, { ...product, quantity: 1 }];
        }
    });
};



const CartPage = ({ cart }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const { productId } = useParams(); 

    const productPhoto = "/Computer.jpg";
    const productName = "MacBook Pro Laptop";
    const individualPrice = 3500;

    const [quantity, setQuantity] = useState(1);
    const [total, setTotal] = useState(individualPrice * quantity);

    const grandTotal = cart.reduce((acc, product) => acc + product.price * product.quantity, 0);

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await fetch(`http://localhost:8080/v1/products/price/${productId}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const data = await response.json();
                setProducts(data);
                setLoading(false);
            } catch (error) {
                console.error("Failed to fetch products:", error);
                setLoading(false);
            }
        }
        fetchData();
    }, [productId]);
    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="cartWrapper">
            <tbody>
            </tbody>
            <h2>Items in cart</h2>
            <table>
                <thead>
                    <tr>
                        <th>Product Photo</th>
                        <th>Product Name</th>
                        <th></th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td><img src={productPhoto} alt="product" width="200" /></td>
                        <td>{productName}</td>
                        <td><button className="removeBtn">Remove</button></td>
                        <td>
                            <span>Qty:</span>
                            <input type="number" value={quantity} readOnly className="quantityInput" />
                        </td>
                        <td>Total: ${total}</td>
                    </tr>
                        {cart.map((product) => (
                            <tr key={product.productID}>
                                <td><img src={'/products/' + product.productID + '.webp'} alt={product.productName} width={70} /></td>
                                <td>{product.productName}</td>
                                <td><button className="removeBtn">Remove</button></td>
                                <td>
                                    <span>Qty:</span>
                                    <input type="number" value={product.quantity || 1} readOnly className="quantityInput" />
                                </td>
                                <td>Total: ${product.price * (product.quantity || 1)}</td>
                            </tr>
                        ))}
                </tbody>
            </table>
            <div className="clearCartGroup">
                <button className="clearCart">Clear Cart</button>
                <span>Grand Total: ${grandTotal}</span>
            </div>
            <div className="buttonsGroup">
                <button className="continueBtn">Continue Shopping</button>
                <button className="checkoutBtn">Proceed To Checkout</button>
            </div>
        </div>
    );
}

export default CartPage;
