// import React, { useEffect, useState, useContext } from 'react';
// import { useParams } from 'react-router-dom';
// import './CartPage.css';
// import { UserContext } from '../App';

import React, { useContext, useEffect } from 'react';
import { UserContext } from '../App';

async function updateCartOnServer(userId, cart) {
  await fetch(`http://localhost:8080/v1/carts/update`, {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ userId, cart })
  });
}


const productPage = (productID) => {
    window.location.href = `/product/${productID}`;
};

const roundedQuantity = (quantity) => {
    return parseFloat(quantity.toFixed(2));
} 

const CartPage = () => {
    // { cart, updateProductQuantity, removeFromCart, clearCart }
    // const [products, setProducts] = useState([]);
    // const [loading, setLoading] = useState(true);
    // const { user } = useContext(UserContext);

    const { cart, setCart, user } = useContext(UserContext);

    //const [cart, setCart] = useState(propsCart);
    //const { user } = useContext(UserContext);

    // const grandTotal = cart.reduce((acc, product) => acc + product.price * product.quantity, 0);
    // const updateQuantity = async (productID, newQuantity) => {
    //     if (newQuantity >= 1) {
    //       await updateProductQuantity(productID, newQuantity);
    //     }
    // };

    // useEffect(() => {
    //     setCart(propsCart);
    // }, [propsCart]);

    const updateProductQuantity = async (productId, newQuantity) => {
        const updatedCart = cart.map(item => 
          item.productId === productId ? {...item, quantity: newQuantity} : item
        );
        setCart(updatedCart);
        if (user?.id) {
          await updateCartOnServer(user.id, updatedCart);
        }
      };

      const removeFromCart = async (productId) => {
        const updatedCart = cart.filter(item => item.productId !== productId);
        setCart(updatedCart);
        if (user?.id) {
          await updateCartOnServer(user.id, updatedCart);
        }
      };

      useEffect(() => {
        if (user?.id) {
          fetch(`http://localhost:8080/v1/carts/${user.id}`)
            .then(res => res.json())
            .then(data => setCart(data.cart));
        }
      }, [user]);


    return (
        <div className="cartWrapper">
            <tbody>
            </tbody>
            <h2>Items in cart</h2>
            <table>
                <thead>
                    <tr>
                        <th>Product Photo</th>
                        <th>Product Name</th>
                        <th></th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    {cart.map((product) => (
                        <tr key={product.productID}>
                            <td>
                                <button onClick={() => productPage(product.productID)} style={{border: 'none', background: 'transparent'}}>
                                    <img src={'/products/' + product.productID + '.webp'} 
                                        alt={"Image of " + product.productName} 
                                        width={70} 
                                        style={{border: '2px solid #000', borderRadius: '5px'}} />
                                </button>
                            </td>
                            <td>{product.productName}</td>
                            <td><button onClick={() => removeFromCart(product.productID)} className="removeBtn">Remove</button></td>
                            <td>
                                <span>Qty:</span>
                                <input type="number" value={product.quantity || 1} onChange={(e) => updateQuantity(product.productID, parseInt(e.target.value))} className="quantityInput" />
                            </td>
                            <td>Total: ${roundedQuantity(product.price * (product.quantity || 1))}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="clearCartGroup">
                <button onClick={clearCart} className="clearCart">Clear Cart</button>
                <span>Grand Total: ${roundedQuantity(grandTotal)}</span>
            </div>
            <div className="buttonsGroup">
                <button className="continueBtn">Continue Shopping</button>
                <button className="checkoutBtn">Proceed To Checkout</button>
            </div>
        </div>
    );
}

export default CartPage;
