import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import {UserContext} from '../App.js';
import './CartPage.css';


const CartPage = () => {
    //const { user } = useContext(UserContext);
    //const [cart, setCart] = useState([]);

    const { user } = useContext(UserContext);
    console.log(user)
    const [cart, setCart] = useState([]);
    const navigate = useNavigate();

    //const navigate = useNavigate();
    const productPage = (productId) => {
        navigate(`/product/${productId}`);
    };

    const roundedQuantity = (num) => Math.round(num * 100) / 100;


    useEffect(() => {
        const fetchCartForUser = async () => {
            const response = await fetch(`http://localhost:8080/v1/carts/customer/${user.UserID}`);
            const data = await response.json();
            if (data) {
                setCart(data);

                const productPromises = Object.keys(data.contents || {}).map(id => 
                    fetch(`http://localhost:8080/v1/products/${id}`)
                    .then(res => res.json())
                    .then(product => {
                        product.quantity = data.contents[id];
                        return product;
                    })
                );
                const fetchedProducts = await Promise.all(productPromises);
                setProducts(fetchedProducts);
            }
        }; // <-- Removed the semicolon from here

        fetchCartForUser();
    }, [user]);


    const updateProductQuantity = async (productId, newQuantity) => {
        if (cart.contents && cart.contents[productId]) {
            cart.contents[productId] = newQuantity;
            setCart({ ...cart });
        }

        try {
            await fetch('http://localhost:8080/v1/carts/set', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    cartId: cart.cartId,
                    productId,
                    quantity: newQuantity
                })
            });

            // Also update local products array for rendering
            const updatedProducts = products.map(product => {
                if (product.productID === productId) {
                    return { ...product, quantity: newQuantity };
                }
                return product;
            });
            setProducts(updatedProducts);

        } catch (error) {
            console.error("Error updating product quantity:", error);
        }
    };

    const handleQuantityChange = async (productId, newQuantity) => {
        if (newQuantity >= 1) {
            const updatedContents = { ...cart.contents, [productId]: newQuantity };
            const updatedCart = { ...cart, contents: updatedContents };
            setCart(updatedCart);

            try {
                await fetch('http://localhost:8080/v1/carts/set', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        cartId: cart.cartId,
                        productId,
                        quantity: newQuantity
                    })
                });

                const updatedProducts = products.map(product => {
                    if (product.productID === productId) {
                        return { ...product, quantity: newQuantity };
                    }
                    return product;
                });
                setProducts(updatedProducts);
            } catch (error) {
                console.error("Error updating product quantity:", error);
            }
        }
    };


    // const handleQuantityChange = (productId, newQuantity) => {
    //     if (newQuantity >= 1) {
    //         updateProductQuantity(productId, newQuantity);
    //     }
    // };

    const removeFromCart = async (productId) => {
        await fetch(`http://localhost:8080/v1/carts/delete`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                cartId: cart.cartId,
                productId
            })
        });

        const updatedCart = cart.filter(product => product.productId !== productId);
        setCart(updatedCart);
    };

    const clearCart = async () => {
        await fetch(`http://localhost:8080/v1/carts/clear/${user.UserID}`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
        });
        setCart([]);
    };

    //const grandTotal = cart.reduce((acc, product) => acc + product.price * product.quantity, 0);
    const grandTotal = products.reduce((acc, product) => acc + product.price * product.quantity, 0);


    const updateQuantity = async (productID, newQuantity) => {
        if (newQuantity >= 1) {
          await updateProductQuantity(productID, newQuantity);
        }
    };


    const getProductIdsByCartId = (cart, cartId) => {
        if (cart && cart.cartID === cartId && cart.contents) {
            return Object.keys(cart.contents).map(Number);
        }
        return [];
    };


    const cartIdToSearch = cart.cartID;
    const productIdsArray = cartIdToSearch ? getProductIdsByCartId(cart, cartIdToSearch) : [];

    console.log(productIdsArray)
    console.log(cart.cartId)
    console.log(cart)
    console.log("ItemsINARRAY")

    const fetchProductData = async (cart) => {
        const productPromises = Object.keys(cart.contents).map(id => 
            fetch(`http://localhost:8080/v1/products/${id}`)
            .then(res => res.json())
            .then(product => {
                product.quantity = cart.contents[id];
                return product;
            })
        );

        const products = await Promise.all(productPromises);
        return products;
    };

    const [products, setProducts] = useState([]);

    // useEffect(() => {
    //     if (cart && cart.contents) {
    //         fetchProductData(cart).then(data => {
    //             setProducts(data);
    //         });
    //     }
    // }, [cart]);



    return (
        <div className="cartWrapper">
            <h2>Items in cart</h2>
            <table>
                <thead>
                    <tr>
                        <th>Product Photo</th>
                        <th>Product Name</th>
                        <th></th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    {/* <p>{productIdsArray.join(', ')}</p> */}
                    {/* {cart.map((product) => ( */}
                        {/* <tr key={product.productID}>
                            <td>
                                <button onClick={() => productPage(product.productID)} style={{border: 'none', background: 'transparent'}}>
                                    <img src={'/products/' + product.productID + '.webp'} 
                                        alt={"Image of " + product.productName} 
                                        width={70} 
                                        style={{border: '2px solid #000', borderRadius: '5px'}} />
                                </button>
                            </td>
                            <td>{product.productName}</td>
                            <td><button onClick={() => removeFromCart(product.productID)} className="removeBtn">Remove</button></td>
                            <td>
                                <span>Qty:</span>
                                <input type="number" value={product.quantity || 1} onChange={(e) => updateQuantity(product.productID, parseInt(e.target.value))} className="quantityInput" />
                            </td>
                            <td>Total: ${roundedQuantity(product.price * (product.quantity || 1))}</td>
                        </tr> */}
                    {/* ))} */}
                    {products.map((product) => (
                        <tr key={product.productID}>
                            <td>
                                <button onClick={() => productPage(product.productID)} style={{border: 'none', background: 'transparent'}}>
                                    <img src={'/products/' + product.productID + '.webp'} 
                                        alt={"Image of " + product.productName} 
                                        width={70} 
                                        style={{border: '2px solid #000', borderRadius: '5px'}} />
                                </button>
                            </td>
                            <td>{product.productName}</td>
                            <td><button onClick={() => removeFromCart(product.productID)} className="removeBtn">Remove</button></td>
                            <td>
                                <span>Qty:</span>
                                {/* <input type="number" value={product.quantity} onChange={(e) => updateQuantity(product.productID, parseInt(e.target.value))} className="quantityInput" /> */}
                                <input 
                                    type="number" 
                                    value={product.quantity} 
                                    onChange={(e) => handleQuantityChange(product.productID, parseInt(e.target.value))}
                                    className="quantityInput" 
                                />
                            </td>
                            <td>Total: ${roundedQuantity(product.price * (product.quantity || 1))}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="clearCartGroup">
                <button onClick={clearCart} className="clearCart">Clear Cart</button>
                <span>Grand Total: ${roundedQuantity(grandTotal)}</span>
            </div>
            <div className="buttonsGroup">
                <button className="continueBtn">Continue Shopping</button>
                <button className="checkoutBtn">Proceed To Checkout</button>
            </div>
            <button onClick={() => console.log(user.Name)} > test Button</button>
        </div>
    );
}
export default CartPage;
