function getLowestPricedItem(productArray) {
    return productArray.sort((a, b) => a.price - b.price)[0];
}

export const addProductWithLowestPriceToCart = async (product) => {
    const [cart] = useState([]);
    try {
        // Fetch all the prices for the given product ID
        const priceResponse = await fetch(`http://localhost:8080/v1/products/price/${product.productID}`);
        const priceDataArray = await priceResponse.json();

        const lowestPricedItem = getLowestPricedItem(priceDataArray);

        if (lowestPricedItem) {
            const productWithLowestPrice = {
                ...product,
                price: lowestPricedItem.price,
                supermarket: lowestPricedItem.supermarket
            };

            // Adding product to cart
            const response = await fetch('http://localhost:8080/v1/carts/add', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    cartid: cart.cartID, // Assuming product has cartId property
                    productid: productWithLowestPrice.productID,
                    quantity: 1
                })
            });

            if (!response.ok) {
                console.error("Error adding product to cart:", await response.text());
            } else {
                //navigate('/CartPage.js'); // This should probably be just '/cart' depending on your routes
            }

        } else {
            console.error(`Failed to fetch price for productID: ${product.productID}`);
        }
    } catch (error) {
        console.error(`Error fetching price for productID: ${product.productID}`, error);
    }
};



const CartPage = () => {
    const { user } = useContext(UserContext);
    const [cart, setCart] = useState([]);
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    const roundedQuantity = (num) => Math.round(num * 100) / 100;

    const productPage = (productId) => {
        navigate(`/product/${productId}`);
    };

    const fetchProductWithLowestPrice = async (id) => {
        const response = await fetch(`http://localhost:8080/v1/products/price/${id}`);
        const priceData = await response.json();

        const lowestPriceObject = priceData.reduce((lowest, current) => {
            return (current.price < lowest.price) ? current : lowest;
        });

        return lowestPriceObject.price;
    };

    useEffect(() => {
        if (!user || !user.UserID) return; 

        const fetchCartForUser = async () => {
            const response = await fetch(`http://localhost:8080/v1/carts/customer/${user.UserID}`);
            const data = await response.json();
            if (data && data.contents) {
                setCart(data);

                const productPromises = Object.keys(data.contents).map(async id => {
                    const product = await fetch(`http://localhost:8080/v1/products/${id}`).then(res => res.json());
                    product.quantity = data.contents[id];
                    product.price = await fetchProductWithLowestPrice(id); 
                    return product;
                });

                const fetchedProducts = await Promise.all(productPromises);
                setProducts(fetchedProducts);
            }
        };

        fetchCartForUser();
    }, [user]);


    const handleQuantityChange = async (productId, newQuantity) => {

        console.log(`Attempting to update product ${productId} with quantity ${newQuantity}`); 
        console.log(cart.cartID);
        console.log(productId);
        console.log(newQuantity);

        if (newQuantity >= 1) {
            const updatedContents = { ...cart.contents, [productId]: newQuantity };
            const updatedCart = { ...cart, contents: updatedContents };
            setCart(updatedCart);

            try {
                await fetch('http://localhost:8080/v1/carts/set', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        cartid: cart.cartID,
                        productid: productId,
                        quantity: newQuantity
                    })
                });


                const updatedProducts = products.map(product => {
                    if (product.productID === productId) {
                        return { ...product, quantity: newQuantity };
                    }
                    return product;
                });
                setProducts(updatedProducts);
            } catch (error) {
                console.error("Error updating product quantity:", error);
            }
        }
    };



    const removeFromCart = async (productId) => {
        await fetch(`http://localhost:8080/v1/carts/delete`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                cartid: cart.cartID,
                productid: productId,
            })
        });

        const updatedProducts = products.filter(product => product.productID !== productId);
        setProducts(updatedProducts);
    };

    const clearCart = async () => {
        for (let product of products) {
            await fetch(`http://localhost:8080/v1/carts/delete`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    cartid: cart.cartID,
                    productid: product.productID,
                })
            });
        }

        setCart(prevCart => ({ ...prevCart, contents: {} }));
        setProducts([]);
    };


    const grandTotal = products.reduce((acc, product) => acc + product.price * product.quantity, 0);

    const getProductIdsByCartId = (cart, cartId) => {
        if (cart && cart.cartID === cartId && cart.contents) {
            return Object.keys(cart.contents).map(Number);
        }
        return [];
    };


    return (
        <div className="cartWrapper">
            <h2>Items in cart</h2>
            <table>
                <thead>
                    <tr>
                        <th>Product Photo</th>
                        <th>Product Name</th>
                        <th></th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.productID}>
                            <td>
                                <button onClick={() => productPage(product.productID)} style={{border: 'none', background: 'transparent'}}>
                                    <img src={'/products/' + product.productID + '.webp'} 
                                        alt={"Image of " + product.productName} 
                                        width={70} 
                                        style={{border: '2px solid #000', borderRadius: '5px'}} />
                                </button>
                            </td>
                            <td>{product.productName}</td>
                            <td><button onClick={() => removeFromCart(product.productID)} className="removeBtn">Remove</button></td>
                            <td>
                                <span>Qty:</span>
                                <input type="number" value={product.quantity} onChange={(e) => {handleQuantityChange(product.productID, parseInt(e.target.value)); }} className="quantityInput" />
                            </td>
                            <td>Total: ${roundedQuantity(product.price * (product.quantity || 1))}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="clearCartGroup">
                <button onClick={clearCart} className="clearCart">Clear Cart</button>
                <span>Grand Total: ${roundedQuantity(grandTotal)}</span>
            </div>
            <div className="buttonsGroup">
                <button className="continueBtn">Continue Shopping</button>
                <button className="checkoutBtn">Proceed To Checkout</button>
            </div>
            <button onClick={() => console.log(user.Name)} > test Button</button>
        </div>
    );
}
export default CartPage;
