import React, { useState } from 'react';
import './CartPage.css';

const CartPage = () => {

    const productPhoto = "/Computer.jpg";
    const productName = "MacBook Pro Laptop";
    const individualPrice = 3500;

    const [quantity, setQuantity] = useState(1);
    const [total, setTotal] = useState(individualPrice * quantity);

    const handleQuantityChange = (direction) => {
        let newQuantity = quantity;
        if (direction === "increase") {
            newQuantity++;
        } else if (direction === "decrease" && quantity > 1) {
            newQuantity--;
        }
        setQuantity(newQuantity);
        setTotal(individualPrice * newQuantity);
    }

    return (
        <div className="cartWrapper">
            <h2>Items in cart</h2>
            <table>
                <thead>
                    <tr>
                        <th>Product Photo</th>
                        <th>Product Name</th>
                        <th></th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td><img src={productPhoto} alt="product" width="200" /></td>
                        <td>{productName}</td>
                        <td><button className="removeBtn">Remove</button></td>
                        <td>
                            <span>Qty:</span>
                            <input type="number" value={quantity} readOnly className="quantityInput" />
                            <button onClick={() => handleQuantityChange("increase")}>↑</button>
                            <button onClick={() => handleQuantityChange("decrease")}>↓</button>
                        </td>
                        <td>Total: ${total}</td>
                    </tr>
                </tbody>
            </table>
            <div className="cartFooter">
                <button className="continueBtn">Continue Shopping</button>
                <button className="clearCart">Clear Cart</button>
                <span>Grand Total: ${total}</span>
                <button className="checkoutBtn">Proceed To Checkout</button>
            </div>
            <div className="footer"></div>
        </div>
    );
}

export default CartPage;
